import { useState } from "react";
import React from "react";
import { Link } from "react-router-dom";

const Menu = () => {
  const [selectedMenu, useSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, useIsProfileDropdownOpen] = useState(false);

  const handleMenuClick = (index) => {
    useSelectedMenu(index)
  }

  return (
    <div className="menu-container">
      <img src="logo.png" style={{ width: "50px" }} />
      <div className="menus">
        <ul>
          <li>
            <p>Dashboard</p>
          </li>
          <li>
            <p>Orders</p>
          </li>
          <li>
            <p>Holdings</p>
          </li>
          <li>
            <p>Positions</p>
          </li>
          <li>
            <p>Funds</p>
          </li>
          <li>
            <p>Apps</p>
          </li>
        </ul>
        <hr />
        <div className="profile">
          <div className="avatar">ZU</div>
          <p className="username">USERID</p>
        </div>
      </div>
    </div>
  );
};

export default Menu;
